/* eslint-disable */
import Vue from "vue";
import Vuex from "vuex";
import axios from "axios";

Vue.use(Vuex);


export const store = new Vuex.Store({
    state: {
        searchString: "",
        filteredList: [],
        jsonData: []
    },
    getters: {
        getFilteredList(state) {
            return state.filteredList;
        },
        getSearchString(state) {
            return state.searchString;
        },
        getJsonData: (state) => {
            axios.get("mockData.json").then((response) => {
                const list = response.data.data;
                for (let i = 0; i < list.length; i++) {
                    const x = list[i];

                    state.jsonData.push({
                        name: x[0],
                        company: x[1],
                        email: x[2],
                        date: x[3],
                        country: x[4],
                        city: x[5],
                    });
                }
            });
            return state.jsonData;
        }
    },
    // State verisini güncelleme
    mutations: {
        setFilteredList: (state, list) => {
            state.filteredList = list;
        },
        setSearchString: (state, data) => {
            state.searchString = data;
        },
        filter: (state, data) => {
            getJsonData();
            this.postList = this.postList.filter((customer) => {
                if (
                    customer.name
                        .toLowerCase()
                        .indexOf(this.searchString.toLowerCase()) >= 0
                )
                    return customer;
            });
        },

    },
    actions: {}
});
