/* eslint-disable */
import Vue from "vue";
import Vuex from "vuex";
Vue.use(Vuex);


export const store = new Vuex.Store({
    state: {
        searchString: "",
        list: []
    },
    getters: {
        getSearchString(state) {
            return state.searchString;
        }
    },
    // State verisini güncelleme
    mutations: {
        getFilteredList: (state, list) => {
            state.searchString = list;
            return state.searchString;
        }
    },
    actions: {}
});
